 assert result.photoUrls !=  null
 assert result.tags.size() == 1
 assert result.tags[0].id == 1
 assert result.tags[0].name == "maisie"
 assert result.status == "available"

 /* assert dataTypes */
 assert result.id.getClass() == Integer 
 assert result.category.id.getClass() == Integer 
 assert result.category.name.getClass() == String 
 assert result.status.getClass() == String
 assert result.tags.getClass() == ArrayList
  assert result.photoUrls.getClass() == ArrayList
 assert result.name.getClass() == String
 assert result.category.getClass() == HashMap
 }
 catch (Exception e)
 {
log.info(e)
 }
finally 
{
 log.info ("JSON message size " + result.size())	
 log.info ("Parsing complete " + result)
}
}

